import React, { Component } from 'react';

class Counter extends Component {
    state = {
        //value: this.props.counter.value,
        //imageUrl: 'https://png.icons8.com/ios/1600/counter.png',
        //tags: ['tag1', 'tag2']
    };

    // constructor() {
    //     super();
    //     this.handleIncrement = this.handleIncrement.bind(this);
    // }

    // handleIncrement = product => {
    //     this.setState({ value: this.state.value + 1 });
    // }

    btnStyle = {
        marginRight: 15
    };

    renderTags() {
        if (this.state.tags.length === 0) return <p>There are no tags!</p>

        return <ul>{ this.state.tags.map(tag => <li key={tag}>{tag}</li>) }</ul>
    }

    render() { 
        return (
            <div className="row">
                <div className="col-md-3">
                   Player #{this.props.counter.id} - {this.props.counter.descr}
                </div>
                <div className="col-md-1">
                    <p className={this.getBadgeClasses()}>{this.formatCount()} wins</p>
                </div>
                <div className="col-md-8">
                    {/* <img src={this.state.imageUrl} alt="" height="150" /> */}
                    <button onClick={() => this.props.onDecrement(this.props.counter)} className="btn btn-secondary btn-sm" style={this.btnStyle}>Decrease</button>
                    <button onClick={() => this.props.onIncrement(this.props.counter)} className="btn btn-success btn-sm" style={this.btnStyle}>Increase</button>
                    <button onClick={() => this.props.onDelete(this.props.counter.id)} className="btn btn-danger btn-sm m-2">Delete player</button>
                    {/* { this.renderTags() } */}
                </div>
            </div>
        );
    }

    getBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += this.props.counter.value === 0 ? "warning" : "primary";
        return classes;
    }

    formatCount() {
        const { value } = this.props.counter;
        //return value === 0 ? 'Zero' : this.props.counter.value;
        return value;
    }

}
 
export default Counter;
