import React, { Component } from 'react';

class Counter extends Component {
    state = {
        value: this.props.value,
        imageUrl: 'https://png.icons8.com/ios/1600/counter.png',
        tags: ['tag1', 'tag2', 'tag3']
    };

    // constructor() {
    //     super();
    //     this.handleIncrement = this.handleIncrement.bind(this);
    // }

    handleIncrement = product => {
        this.setState({ value: this.state.value + 1 });
    }

    renderTags() {
        if (this.state.tags.length === 0) return <p>There are no tags!</p>

        return <ul>{ this.state.tags.map(tag => <li key={tag}>{tag}</li>) }</ul>
    }

    render() { 
        return (
            <div>
                <h4>Counter #{this.props.id}</h4>
                <img src={this.state.imageUrl} alt="" height="150" />
                <br/>
                <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
                <button onClick={() => this.handleIncrement(1)} className="btn btn-secondary btn-sm">Increment</button>

                { this.renderTags() }
            </div>
        );
    }

    getBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += this.state.value === 0 ? "warning" : "primary";
        return classes;
    }

    formatCount() {
        const { value } = this.state;
        return value === 0 ? 'Zero' : this.state.value;
    }

}
 
export default Counter;
